//init smooth-scroll to another page
function initSmoothScrollToAnotherPage() {

	var myHash = location.hash;
	location.hash = '';
	if(myHash[1] != undefined){
		jQuery('html, body').animate(
			{scrollTop: jQuery(myHash).offset().top - 50}
			, 700);
		location.hash = myHash;
	};
}

// preloader init
function initPreloader() {
	jQuery(function() {
		jQuery('.preloader-bg').find('.box-img').fadeOut().end().delay(400).fadeOut('slow');
	});
}

// Smooth Scrollinit
function initSmoothScroll() {

	var ScrollTop = jQuery(".back-to-top");

	window.onscroll = function() {scrollFunction()};
	function scrollFunction() {
		if (document.body.scrollTop > 1500 || document.documentElement.scrollTop > 1500) {
			ScrollTop.addClass("active");
		} else {
			ScrollTop.removeClass("active");
		}
	}

	ScrollTop.on('click',function (e) {
		e.preventDefault();
		var target = this.hash,
		jQuerytarget = jQuery(target);
		jQuery('html, body').stop().animate({
			'scrollTop': jQuerytarget.offset().top
		}, 800, function () {
			window.location.hash = target;
		});
	});
}

